mapper.select:
select
  id, created_at, updated_at, editor_id, cover_url, status, artwork_url
from art_room.banner


        and id = #{id}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and editor_id = #{editorId}


        and cover_url = #{coverUrl}


        and status = #{status}


        and artwork_url = #{artworkUrl}
