mapper.select:
select
  id, created_at, updated_at, editor_id, artwork_id, status, parent_id, content
from art_room.comment_reply
where status = #{status}
AND artwork_id = #{artworkId}
ORDER BY updated_at ${orderBy}

select
  id, created_at, updated_at, editor_id, artwork_id, status, parent_id, content
from art_room.comment_reply


        and id = #{id}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and editor_id = #{editorId}


        and artwork_id = #{artworkId}


        and status = #{status}


        and parent_id = #{parentId}


        and content = #{content}
