mapper.select:
select
  id, created_at, updated_at, editor_id, name, status, level, parent_id, children_ids
from art_room.manu


        and id = #{id}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and editor_id = #{editorId}


        and name = #{name}


        and status = #{status}


        and level = #{level}


        and parent_id = #{parentId}


        and children_ids = #{childrenIds}
